    entry->reserve1_len = STRLEN;     entry->reserve2_len = STRLEN;
    entry->reserve3_len = STRLEN;     entry->reserve4_len = STRLEN;
    entry->reserve5_len = STRLEN;     entry->reserve6_len = STRLEN;
